export const experiments = function (...params) {
    if (params.length === 1) {
        this.hooks.on(`build.experiments`, (experiments = {}) => ({
            ...experiments,
            ...params[0],
        }));
    }
    else if (typeof params[0] === `string`) {
        this.hooks.on(`build.experiments`, (experiments = {}) => ({
            ...experiments,
            [`${params[0]}`]: params[1],
        }));
    }
    return this;
};
