import { Bud } from '../../index.js';
/**
 * Execute a function after compiler has finished
 */
export const after = function (action, errorHandler) {
    this.hooks.action(`compiler.close`, async (bud) => {
        try {
            await action(bud);
        }
        catch (error) {
            if (!errorHandler)
                throw error;
            errorHandler(error);
        }
    });
    return this;
};
